import React from "react";
import TuitStats from "./tuits-stats";
import { deleteTuit } from "./tuits-reducer";
import { autoBatchEnhancer } from "@reduxjs/toolkit";
import { useDispatch } from "react-redux";


// const dispatch = useDispatch();
// const deleteTuitHandler = (id) => {
//     dispatch(deleteTuit(id));
// }

const TuitItem_a7 = (
    {
        tuit = {
            userName: "Novak Djokovic",
            time: "50mins",
            title: "I'm very grateful for the kind of energy and reception and love and support I got last night.",
            image: "Novak_400x400.jpeg",
            liked: true,
            replies: 2300,
            retuits: 4321,
            likes: 3456,
            handle: "@novak_djokovic",
            tuit: "\"I'm super blessed and grateful...\" paid tribute to the team behind his phenomenal success on the eve of another Grand Slam final. ",
        }
    }
) => {
    return (

        <li className="list-group-item">
            <div className="row">
                <div className="col-auto mt-1">
                    <img className="float-end rounded-circle" width={48} src={`/images/${tuit.image}`} alt='Avatar Image' />
                </div>

                <div className="col-10">

                    <div>
                        {/* <i className="bi bi-x-lg float-end"
                            onClick={() => deleteTuitHandler(tuit._id)}></i> */}
                        <i className="bi bi-x-lg float-end"></i>

                        <span className="fw-bold"> {tuit.userName} </span>
                        <span>
                            <i class="bi bi-patch-check-fill text-primary"></i>
                        </span>
                        <span> {tuit.handle} â€¢ {tuit.time}</span>
                    </div>

                    <div>
                        <p>{tuit.title}</p>
                    </div>

                    <div className="wd-img-content-style wd-post-border wd-rounded-corners">
                        <span>
                            {tuit.tuit && <p >{tuit.tuit}</p>}
                        </span>
                    </div>

                    <TuitStats />

                </div>

            </div>
        </li>

    );
};

export default TuitItem_a7;
