import React, { useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { updateAdmin } from "../profile/profile-reducer";

// import TuitsList from "../tuits";
// import WhatsHappening from "./whats-happening";

const EditProfile = () => {

    const { user } = useSelector((state) => state.user);
    const [profile, setProfile] = useState(user);
    // const dispatch = useDispatch();
    // const handleSaveButton = () => {
    //     dispatch(updateAdmin(profile));
    // }


    return (
        <>
            <div className="row">
                <i className="col-1 bi bi-x"> </i>
                <h4 className="col-9">Edit Profile</h4>
                <button className="col-2 btn btn-dark rounded-5 float-end">Save</button>
            </div>

            {/* <div>
                <span className="fw-bold">{profile.followingCount}</span>
                <span className="text-secondary"> Following </span>
                &nbsp;&nbsp;
                <span className="fw-bold">{profile.followersCount}</span>
                <span className="text-secondary"> Followers </span>
            </div> */}

            {/* <h2 className="mt-5"> EditProfile component</h2> */}

            <label>First Name</label><br />
            <input
                placeholder="Name"
                title="Title"
                value={profile.firstName}
                onChange={(e) => setProfile({
                    ...profile,
                    firstName: e.target.value,
                })} /><br /><br />

            <label>Last Name</label><br />
            <input value={profile.lastName}
                onChange={(e) => setProfile({
                    ...profile,
                    lastName: e.target.value,
                })} /><br /><br />

            <label>Bio</label><br />
            <input

                // style="width:200px; height:50px;" 

                value={profile.bio}
                onChange={(e) => setProfile({
                    ...profile,
                    bio: e.target.value,
                })} /><br /><br />

            <label>Phone</label><br />
            <input
                value={profile.location}
                onChange={(e) => setProfile({
                    ...profile,
                    location: e.target.value,
                })} /><br /><br />

            <label>website</label><br />
            <input
                value={profile.website}
                onChange={(e) => setProfile({
                    ...profile,
                    address: e.target.value,
                })} /><br /><br />

            <label>Birt date</label>   <button>Edit</button>
            <input
                value={profile.website}
                onChange={(e) => setProfile({
                    ...profile,
                    address: e.target.value,
                })} /><br /><br />

            <br /><br />




        </>
    );
};

export default EditProfile;
