const TuitItem_a7 = (
    {
        tuit_post = {
            userName: "Novak Djokovic",
            time: "50mins",
            title: "I'm very grateful for the kind of energy and reception and love and support I got last night.",
            image: "Novak_400x400.jpeg",
            liked: true,
            replies: 2300,
            retuits: 4321,
            likes: 3456,
            handle: "@novak_djokovic",
            tuit: "\"I'm super blessed and grateful...\" paid tribute to the team behind his phenomenal success on the eve of another Grand Slam final. "
        }
    }
) => {
    return (

        <li className="list-group-item">
            <div className="row">
                <div className="col-auto mt-1">
                    <img className="float-end rounded-circle" width={48} src={`/images/${tuit_post.image}`} alt='Avatar Image' />
                </div>
                <div className="col-10">
                    <div>
                        {/* <i className="bi bi-x-lg float-end"
                            onClick={() => deleteTuitHandler(tuit._id)}></i> */}
                        <i className="bi bi-x-lg float-end"></i>
                        <span className="fw-bold"> {tuit_post.userName} </span>
                        <i className="bi bi-patch-check-fill text-primary"></i>
                        <span className="text-secondary"> {tuit_post.handle} â€¢ {tuit_post.time} </span>
                    </div>
                    <p>{tuit_post.title}</p>
                    <div className="text-secondary">
                        {tuit_post.tuit && <p >{tuit_post.tuit}</p>}
                    </div>
                    <TuitStats />
                </div>
            </div>
        </li>
    );
};

export default TuitItem_a7;
