import React from "react";
import TuitItem from "./tuits-item";
import { useSelector } from "react-redux";

const TuitsList = () => {
    const tuitArray = useSelector(       // retrieve state from the store
        (state) => state.tuits
    )

    return (

        <ul className="list-group">
            {
                tuitArray.map(tuits =>
                    <TuitItem
                        key={tuits._id}
                        tuits={tuits} />
                )
            }
        </ul>
    );
};

export default TuitsList;
