import React from "react";
import TuitItem_a7 from "./tuits-item.js";

import { useSelector } from "react-redux";
import PostSummaryItem from "../post-summary-list/post-summary-item.js";

const TuitsList_a7 = () => {
    const tuitsArray = useSelector(       // retrieve state from the store
        state => state.tuits
    );

    return (
        <ul className="list-group">
            {
                tuitsArray.map(tuit =>
                    <PostSummaryItem
                        key={tuit._id}
                        tuit={tuit}
                    />
                )
            }
        </ul>
    );
};

export default TuitsList_a7;
