import { createSlice } from "@reduxjs/toolkit"

const initialState = {
    todos: [
        { id: 1, text: "Learn React-Redux I", completed: true, color: "blue" },
        { id: 2, text: "Learn React-Redux II", completed: false, color: "red" },
        { id: 3, text: "Learn React-Redux III", completed: true, color: "green" },
        { id: 4, text: "Learn React-Redux VI", completed: false, color: "orange" },
    ]
}

const todosSlice = createSlice(
    {
        name: "todos",
        initialState,
        reducers: {
            deleteTodo: (state, action) => {
                state.todos = state.todos.filter((todo) => todo.id !== action.payload)
            }
        },

    }
);

export default todosSlice.reducer;
// export { deleteTodo }
