import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { decreaseCount, increaseCount, updateCount } from "./counter-reducer";

function CounterScreen() {

    const { count } = useSelector((state) => state.counter);

    const [counter, setCounter] = useState(count);

    const dispatch = useDispatch();
    const handleSaveCounter = () => {
        dispatch(updateCount(counter));
    }

    return (
        <div>
            <h1>Counter Screen</h1>
            <h2>{ counter }</h2>

            {/* <button onClick={ () => setCounter(counter + 1)}> + </button>
            <button onClick={ () => setCounter(counter - 1)}> -  </button> */}
            <button onClick={ () => increaseCount(counter + 1) }> + </button>
            <button onClick={ () => decreaseCount(counter - 1) }> - </button>

            <br/><br/>
            <button 
                onClick={dispatch(updateCount(counter))}
                className="btn btn-secondary">
                Save 
            </button>

        </div>
    );
}

export default CounterScreen;
