                onChange={(e) => setProfile({
                    ...profile,
                    lastName: e.target.value,
                })} /><br /><br /> */}

            <label>Bio</label><br />
            <textarea name="Text1" cols="60" rows="5" value={profile.bio} onChange={(e) => setProfile({
                ...profile,
                bio: e.target.value,
            })}></textarea>

            {/* <input
                value={profile.bio}
                onChange={(e) => setProfile({
                    ...profile,
                    bio: e.target.value,
                })} /> */}

            <br /><br />

            <label>Location</label><br />
            <input
                value={profile.location}
                onChange={(e) => setProfile({
                    ...profile,
                    location: e.target.value,
                })} /><br /><br />

            <label>website</label><br />
            <input
                value={profile.website}
                onChange={(e) => setProfile({
